import classes from './cart.module.css';
import Modal from '../UI/Modal';
import { useContext } from 'react';
import CartContext from '../../store/cart-context';


const Cart = (props)=>{

    const cartContext = useContext(CartContext);

    const totalAmount = `$${cartContext.totalAmount.toFixed(2)}`;

    const hasItems = cartContext.items.length >0;

    const Cartitems =<ul  className={['cart-items']}>
                            {[{id:'c1', name:'biryani',quantity:2,price:12.99}].map(item=>
                            <li>{item.name}</li>)} 
                    </ul>;
return<Modal onClose ={props.onClickClose}>
    {Cartitems}
    <div className={classes.total}> 
        <span>Total Amount</span>
        <span>{totalAmount}</span>
    </div>
     <div className={classes.actions}>
        <button className={classes['button--alt']} onClick={props.onClickClose}>close</button>
         if(hasItems && <button className={classes.button}>Order</button>);

     </div>
</Modal>

}

export default Cart;
