import {useRef,useState,useEffect} from 'react';

const SimpleInput = (props) => {

  const nameInpuRef = useRef();
  const [entreredName, setEnteredName] = useState('');
  //const [nameIsValid, setNameIsValid] = useState(false);
  const [enteredNameTouched,setEnteredNameTouched] = useState(false);
  const nameIsValid = entreredName.trim() !== '';
  const nameInputIsInvalid = !nameIsValid && enteredNameTouched;

  let formIsValid = false;

  if(nameIsValid){
    formIsValid = true;
  }


  // useEffect(()=>{
  //   if(nameIsValid){
  //     console.log('Name Input Is Valid!!')
  //   }
  // },[nameIsValid])


  const nameInputHandler = event=>{
      setEnteredName(event.target.value);
  };

  const nameInputBlurHandler = event=>{
     setEnteredNameTouched(true);
    // if(entreredName.trim() ===''){
    //     setNameIsValid(false);
    //     return;}

  }

  const formSubmitHandler = event =>{

    event.preventDefault();
    setEnteredNameTouched(true);
    if(!nameIsValid){
        return;
    }

    //setNameIsValid(true);
    console.log(entreredName);

    const enteredValue = nameInpuRef.current.value;
    console.log(enteredValue);

    setEnteredName('');
    setEnteredNameTouched(false);
  }

  //const nameInputIsValid = !nameIsValid && enteredNameTouched;

  const nameInputClass = nameInputIsInvalid? 'form-control invalid':'form-control';

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputClass}>
        <label htmlFor='name'>Your Name</label>
        <input ref={nameInpuRef}
               type='text' 
              id='name' 
              onChange={nameInputHandler}
              onBlur={nameInputBlurHandler}
              value={entreredName}/>
             {nameInputIsInvalid && <p className='error-text'>Name field should not be empty</p>} 
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};
export default SimpleInput;
