const emailReducer = (state,action)=>{

  if(action.type === 'userEntering'){

    return { val :action.VALUE, isValid: action.VALUE.includes("@")};
  }
   if(action.type === 'userEntryFinish'){

    return { val :state.val, isValid: state.val.includes("@")};
  }
  return{val:'',isValid : false};

}

const passwordReducer = (state,action)=>{
  // console.log("password hitting")
  if(action.type === 'userEntering'){
    return { val :action.VALUE, isValid: action.VALUE.trim().length >6};
  }
   if(action.type === 'userEntryFinish'){
    return { val :state.val, isValid: state.val.trim().length >6};
  }
  return{val:'',isValid : false};

}
const Login = (props) => {

  //We can combinly Implement the two states with the useReducer Hook let's write here
  const[enteredEmail,dispatchEnteredEmail] = useReducer(emailReducer,{val:'',isValid:null})
  const [formIsValid, setFormIsValid] = useState(false);
  const[enteredPassword,dispatchEnteredPassword] = useReducer(passwordReducer,{ val:'', isValid:null })


  useEffect(() => {
    console.log('EFFECT RUNNING');

    return () => {
      console.log('EFFECT CLEANUP');
    };
  }, []);


  const { isValid: emailIsValid } = enteredEmail;
